import React, { useEffect, useState } from "react";
import Navbar from "../Navbar";
import ProductDetail from "./ProductDetail"; 
import "../../css/productList.css";

const ProductList = () => {
  const [productList, setProductList] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const response = await fetch("http://localhost:8080/products/getproducts", {
        method: "GET",
        header: { "Content-Type": "application/json" },
      });
      const data = await response.json();
      setProductList(data);
    };
    fetchData();
  }, []);

  // Function to handle click on a product card
  const handleProductClick = (product) => {
    setSelectedProduct(product);
  };

  return (
    <div>
      <Navbar />
      <h1>Product List</h1>
      <div className="product-list">
        {productList.map((product) => (
          <div className="card" key={product.productId} onClick={() => handleProductClick(product)}>
            <img src={product.imageUrls[0]} alt={product.name} />
            <div className="card-body">
              <h2 className="card-title">{product.name}</h2>
            </div>
          </div>
        ))}
      </div>
      {/* Render ProductDetail component if a product is selected */}
      {selectedProduct && <ProductDetail product={selectedProduct} />}
    </div>
  );
};

export default ProductList;
